import React from 'react';
var cached = {};
var MangoUtils = {
  /**
   * 注册model
   * @param app
   * @param model
   */
  registerModel: function registerModel(app, model) {
    if (!cached[model.namespace]) {
      app.model(model);
      cached[model.namespace] = 1;
    }
  },

  /**
   * 消息触发
   */
  dispatch: function dispatch(cnx, namespace, type, payload) {
    cnx.props.dispatch({
      type: namespace + '/' + type,
      payload: payload
    });
  },

  /**
   * 在model中处理自发延迟
   */
  delay: function delay(ms) {
    return function () {
      return new Promise(function (resolve) {
        setTimeout(resolve, ms);
      });
    };
  }
};
export default MangoUtils;
